import assert from 'power-assert'
import {
  getFieldUidsAndPatients,
} from '../src/ppt-field'

describe('test util/ppt-field', () => {
  it('should get FieldUids And Patients', () => {
    const array = ['$0[0] + $1[2] / $23[12][0]', '$1[3] - $0[0]', '$12[2]']
    const { fieldIndexes, patientIndexes } = getFieldUidsAndPatients(array)
    assert.deepStrictEqual(fieldIndexes, [0, 1, 12, 23])
    assert.deepStrictEqual(patientIndexes, [0, 2, 3, 12])
  })
})
